d7abc1c56bcdf755c8c656aac82cf2394f2b5a8c.source:
mutation CreateIssue($teamId: String!, $title: String!, $description: String) {
  issueCreate(input: {teamId: $teamId, title: $title, description: $description}) {
    __typename
    success
  }
}

e1f35979e48c2a297b441d7a4f45f86134c2ee08.source:
query GetTeamIssues($teamId: String!, $first: Int, $after: String) {
  team(id: $teamId) {
    __typename
    issues(first: $first, after: $after) {
      __typename
      nodes {
        __typename
        id
        title
        estimate
        createdAt
        updatedAt
        archivedAt
        description
        assignee {
          __typename
          id
          name
        }
      }
    }
  }
}

d31296a981ec59c904e044b4ae9f2031e4da75b4.source:
query GetTeams($first: Int, $after: String) {
  teams(first: $first, after: $after) {
    __typename
    nodes {
      __typename
      id
      name
      description
    }
  }
}
